sys_variable_value.value:
(function execute(inputs, outputs) {

  var customerGr = inputs.tripGr.customer.getRefRecord	();
  var cMiles = parseInt(customerGr.getValue('miles'), 10);
  var cDollars = parseInt(customerGr.getValue('dollars'), 10);
  var tMiles = parseInt(inputs.tripGr.getValue('miles'), 10);
  var tDollars = parseInt(inputs.tripGr.getValue('price'), 10);

  outputs.miles = cMiles + tMiles;
  outputs.dollars = cDollars + tDollars;

})(inputs, outputs);

